import numpy as npimport matplotlib.pyplot as pltfrom sklearn.metrics import r2_score#mean,median,mode,standard deviation, variencespeed = [99,86,87,88,111,86,103,87,94,78,77,85,86]x = np.median(speed)print(x)y=np.std(speed)print(y)z=np.mean(speed)print(z)a=np.var(speed)print(a)#percentileages = [5,31,43,48,50,41,7,11,15,39,80,82,32,2,8,6,25,36,27,61,31]b = np.percentile(ages, 75)print(b)c=np.percentile(ages,90)print(c)#data distributiond=np.random.uniform(0.0,10.0,250)print(d)plt.hist(d,10)plt.show()e=np.random.uniform(0.0,10.0,100000)plt.hist(e,100)plt.show()f=np.random.normal(0.0,10.0,100000)plt.hist(f,100)plt.show()#scatter plotsx1 = np.random.normal(5.0, 1.0, 1000)y1 = np.random.normal(10.0, 2.0, 1000)plt.scatter(x1, y1)plt.show()#polynomial regressionval1=[1,2,3,5,6,7,8,9,10,12,13,14,15,16,18,19,21,22]val2= [100,90,80,60,60,55,60,65,70,70,75,76,78,79,90,99,99,100]modelpol=np.poly1d(np.polyfit(val1,val2,3))linepol=np.linspace(1,22,100)plt.scatter(val1,val2)plt.plot(linepol,modelpol(linepol))plt.show()print(r2_score(val2, modelpol(val1)))