from sklearn.preprocessing import StandardScalerfrom sklearn import linear_modelimport pandas as pd#scaling scale = StandardScaler()df = pd.read_csv('mtcars.csv')X = df[['wt', 'hp']] y = df['mpg']scaledX = scale.fit_transform(X)regr = linear_model.LinearRegression()regr.fit(scaledX, y)print(scaledX)#predicted valuescaled = scale.transform([[3, 150]])predicted = regr.predict(scaled)print("\nThe predicted value is: ",predicted)